import React, { useState, createContext } from 'react';

import api from '../services/api';

const AuthContext = createContext();

export const AuthEstabelecimentoProvider = ({ children }) => {
    const [estabelecimento, setEstabelecimento] = useState(null);

    async function signIn(credentials) {
        try {
            const payload = {
                "Cnpj": credentials.cnpj,
                "Senha": credentials.senha
            }

            const response = await api.post('/api/loginestabelecimento', payload);

            setEstabelecimento(response.data);
            localStorage.setItem('@TBAuth:estabelecimento', JSON.stringify(response.data));
            localStorage.setItem('@TBAuth:token', response.data.accessToken);

            return response;
        } catch (err) {
            return err.response;
        }
    }

    function signOut() {
        setEstabelecimento(null);
    }

    return (
        <AuthContext.Provider value={{ signed: !!estabelecimento, estabelecimento, signIn, signOut }}>
            {children}
        </AuthContext.Provider>
    )
};

export default AuthContext;
