import React, { useState, useEffect, useContext } from 'react';
import Swal from 'sweetalert2';

import AuthEstabelecimentoContext from '../../../../contexts/auth-estabelecimento';
import GeralContext from '../../../../contexts/geral';

import api from '../../../../services/api';
import Loading from '../../../../components/Loading';

export default function ListaFuncionarios() {
    const { estabelecimento } = useContext(AuthEstabelecimentoContext);
    const { isLoadingVisible, setLoadingVisible } = useContext(GeralContext);

    useEffect(() => {

    }, []);

    useEffect(() => {
    }, [estabelecimento]);

    async function handleCadastrar() {
        Swal.mixin({
            input: 'text',
            confirmButtonText: 'Pesquisar',
            showCancelButton: true,
            progressSteps: ['1'],
            inputValidator: value => {
                return new Promise(resolve => {
                    if (value) {
                        resolve()
                    } else {
                        resolve('CPF inválido');
                    }
                });
            }
        }).queue([
            {
                title: 'Pesquisar Usuário',
                text: 'Digite o CPF'
            }
        ]).then(async result => {
            console.log(result.value);
            if (result.value) {
                try {
                    const cpf = (result.value[0])
                    const pesquisaUsuario = await getUsuario(cpf);

                    if (pesquisaUsuario.cpf) {
                        const { value } = Swal.fire({
                            title: 'Cadastrar Funcionário',
                            html: `Nome: ${pesquisaUsuario.nome}
                                    <pre>cpf: ${pesquisaUsuario.cpf}</pre>`,
                            input: 'select',
                            inputOptions: tiposFuncionario.map(item => item.descricao),
                            inputPlaceholder: 'Selecione a função',
                            confirmButtonText: 'Cadastrar',
                            showCancelButton: true,
                            inputValidator: (value) => {
                                return new Promise((resolve) => {
                                    if (value)
                                        resolve()
                                    else
                                        resolve('Você precisa selecionar uma função');
                                });
                            }
                        }).then(result => {
                            const idTipoFuncionario = tiposFuncionario[result.value].id;
                            cadastrarFuncionario(pesquisaUsuario.id, idTipoFuncionario);
                        });
                    } else {
                        Swal.fire('CPF não localizado', 'Tente novamente', 'error');
                    }
                } catch (err) {
                    Swal.fire('CPF não localizado', 'Tente novamente', 'error');
                }
            }
        });
    }

    return (
        <div className="content-wrapper">

            {/* Content Header (Page header) */}
            <section className="content-header">
                <div className="container-fluid">
                    <div className="row mb-2">
                        <div className="col-sm-6">
                            <h1>Cardápio</h1>
                        </div>
                        <div className="col-sm-6">
                            <ol className="breadcrumb float-sm-right">
                                <li className="breadcrumb-item"><a href="#">Home</a></li>
                                <li className="breadcrumb-item active">Cardápio</li>
                            </ol>
                        </div>
                    </div>
                </div>{/* /.container-fluid */}
            </section>

            {/* Main content */}
            <section className="content">
                <div className="container-fluid">

                    <div className="card">
                        <div className="card-header">
                            <h3 className="card-title">Lista de itens do Cardápio</h3>
                            <div className="card-tools">
                                <button className="btn btn-success btn-sm" onClick={handleCadastrar}>
                                    <i className="fas fa-user-plus mr-2"></i>
                                                    Novo
                                </button>
                            </div>
                        </div>
                        <div className="card-body p-0">
                            <table className="table table-striped projects">
                                <thead>
                                    <tr>
                                        <th style={{ width: '5%' }}>
                                            ID
                                        </th>
                                        <th style={{ width: '20%' }}>
                                            Título
                                        </th>
                                        <th style={{ width: '15%' }}>
                                            Categoria
                                        </th>
                                        <th style={{ width: '30%' }}>
                                            Descrição
                                        </th>
                                        <th style={{ width: '10%' }}>
                                            Valor
                                        </th>
                                        <th style={{ width: '20%' }}></th>
                                    </tr>
                                </thead>

                                <tbody>
                                    <tr>
                                        <td>01</td>
                                        <td><a>Nome do item</a></td>
                                        <td><a>Categoria do item</a></td>
                                        <td><a>Descrição do item</a></td>
                                        <td><a>R$ 0.00</a></td>
                                        <td className="project-actions text-right">
                                            <button className="btn btn-primary btn-sm ml-3">
                                                <i className="fas fa-eye mr-2"></i>
                                                    Visualizar
                                                </button>
                                            <button className="btn btn-danger btn-sm ml-3">
                                                <i className="fas fa-trash mr-2"></i>
                                                    Deletar
                                                </button>
                                        </td>
                                    </tr>

                                </tbody>
                            </table>
                        </div>
                        {/* /.card-body */}

                    </div>

                </div>
            </section>
            {/* /.content */}
            {isLoadingVisible ? <Loading /> : null}

        </div>
    );
}
