import React, { useState, useEffect } from 'react';
import { useForm } from "react-hook-form";

import './styles.css';

import api from '../../services/api';

function valoresIniciais() {
    return {
        nome: '',
        cnpj: '',
        endereco: '',
        numero: '',
        latitude: '',
        longitude: '',
        tipoEstabelecimento: '',
        senha: '',
    };
}

export default function CadastroEstabelecimento() {
    const [valores, setValores] = useState(valoresIniciais);
    const [tiposEstabelecimento, setTiposEstabelecimento] = useState([]);

    const { register, handleSubmit, errors } = useForm();

    const onSubmit = data => {
        console.log(data);
    };

    function handleChange(event) {
        const { name, value } = event.target;
        setValores({ ...valores, [name]: value });
    }

    async function handleCadastrar() {
        const payload = {
            "Nome": valores.nome,
            "Cnpj": valores.cnpj,
            "Endereco": valores.endereco,
            "Numero": valores.numero,
            "Latitude": valores.latitude,
            "Longitude": valores.longitude,
            "IdTipoEstabelecimento": valores.tipoEstabelecimento,
            "Imagem": "/uploads/default.png",
            "Distancia_km": "0",
            "Senha": valores.senha
        }

        try {
            const resposta = await api.post('/estabelecimento', payload);
            console.log({ resposta });
        } catch (err) {
            console.log({ err });
        }
    }

    return (
        <div className="hold-transition register-page">
            <div className="register-box">
                <div className="register-logo">
                    <a href="../../index2.html"><b>Tingle</b>Bar</a>
                </div>

                <div className="card">
                    <div className="card-body register-card-body">
                        <p className="login-box-msg">Cadastrar novo estabelecimento</p>

                        <form autoComplete="off" onSubmit={handleSubmit(onSubmit)}>

                            <div className="row">
                                <div className="mb-3 col-md-12">
                                    <input
                                        name="nome"
                                        placeholder="Nome do estabelecimento"
                                        className="form-control"
                                        type="text"
                                        onChange={handleChange}
                                        ref={register({
                                            required: {
                                                value: "Required",
                                                message: "O nome do estabelecimento é obrigatório"
                                            }
                                        })}
                                    />
                                    <p className="text-validation-error">{errors.nome && errors.nome.message}</p>
                                </div>
                            </div>

                            <div className="row">
                                <div className="mb-3 col-md-12">
                                    <input
                                        type="text"
                                        className="form-control"
                                        name="cnpj"
                                        onChange={handleChange}
                                        ref={register({
                                            required: {
                                                value: "Required",
                                                message: "O CNPJ é obrigatório"
                                            },
                                            pattern: {
                                                value: /^\d{2}\.\d{3}\.\d{3}\/\d{4}\-\d{2}$/,
                                                message: "CNPJ inválido"
                                            }
                                        })}
                                    />
                                    <p className="text-validation-error">{errors.cnpj && errors.cnpj.message}</p>
                                </div>
                            </div>

                            <div className="row">
                                <div className="mb-3 col-md-9">
                                    <input
                                        name="endereco"
                                        placeholder="Endereço"
                                        className="form-control"
                                        type="text"
                                        onChange={handleChange}
                                        ref={register({
                                            required: {
                                                value: "Required",
                                                message: "O endereço é obrigatório"
                                            }
                                        })}
                                    />
                                    <p className="text-validation-error">{errors.endereco && errors.endereco.message}</p>
                                </div>
                                <div className="mb-3 col-md-3">
                                    <input
                                        name="numero"
                                        placeholder="Nº"
                                        className="form-control"
                                        type="number"
                                        onChange={handleChange}
                                        ref={register({
                                            required: {
                                                value: "Required",
                                                message: "O número é obrigatório"
                                            },
                                            maxLength: {
                                                value: 5,
                                                message: "O número máximo de caracteres é '5'"
                                            }
                                        })}
                                    />
                                    <p className="text-validation-error">{errors.numero && errors.numero.message}</p>
                                </div>
                            </div>

                            <div className="row">
                                <div className="mb-3 col-md-6">
                                    <input type="text" className="form-control" placeholder="latitude" />
                                </div>
                                <div className="mb-3 col-md-6">
                                    <input type="text" className="form-control" placeholder="longitude" />
                                </div>
                            </div>

                            <div className="row">
                                <div className="mb-3 col-md-12">
                                    <select
                                        className="form-control"
                                        name="tipoEstabelecimento"
                                    >
                                        <option value="">Tipo de estabelecimento</option>
                                        <option value={1}>Bar</option>
                                        <option value={2}>Restaurante</option>
                                        <option value={3}>Lanchonete</option>
                                    </select>
                                </div>
                            </div>

                            <div className="input-group mb-3">
                                <input type="password" className="form-control" placeholder="Senha" />
                                <div className="input-group-append">
                                    <div className="input-group-text">
                                        <span className="fas fa-lock" />
                                    </div>
                                </div>
                            </div>

                            <div className="input-group mb-3">
                                <input type="password" className="form-control" placeholder="Redigite a senha" />
                                <div className="input-group-append">
                                    <div className="input-group-text">
                                        <span className="fas fa-lock" />
                                    </div>
                                </div>
                            </div>

                            <div className="row">
                                <div className="mb-2 col-12">
                                    <div className="icheck-primary">
                                        <input type="checkbox" id="agreeTerms" name="terms" defaultValue="agree" />
                                        <label htmlFor="agreeTerms">
                                            &nbsp;&nbsp;Eu aceito todos os <a href="#">termos de uso</a>
                                        </label>
                                    </div>
                                </div>
                            </div>

                            <div className="row">
                                <div className="mb-2 col-12">
                                    <button type="submit" className="btn btn-primary btn-block">Cadastrar</button>
                                </div>
                            </div>

                        </form>

                        <a href="login.html" className="text-center">Já tenho uma conta de acesso</a>
                    </div>
                    {/* /.form-box */}
                </div>{/* /.card */}
            </div>
            {/* /.register-box */}
        </div>

    );
}
